INSERT INTO `Paintings` (`PaintingID`,`ArtistID`,`GalleryID`,`ImageFileName`,`Title`,`ShapeID`,`MuseumLink`,`AccessionNumber`,`CopyrightText`,`Description`,`Excerpt`,`YearOfWork`,`Width`,`Height`,`Medium`,`Cost`,`MSRP`,`GoogleLink`,`GoogleDescription`,`WikiLink`,`JsonAnnotations`) VALUES (612,102,36,'102050','The Family of Jan Brueghel the Elder',1,'https://courtauld.ac.uk/gallery/collection/baroque/peter-paul-rubens-the-family-of-jan-brueghel-the-elder','P.1978.PG.362','&copy; The Courtauld Gallery','This tender portrait celebrates family and friendship. In it Rubens portrays his dear friend Jan Brueghel the Elder, with his wife and children. Rubens and Brueghel jointly produced many paintings. In these Rubens painted the figures, and Brueghel the animals.','This tender portrait celebrates family and friendship. In it Rubens portrays his dear friend Jan Brueghel the Elder, with his wife and children. Rubens and Brueghel jointly produced many paintings. In these Rubens painted the figures, and Brueghel the animals.',1614,95,125,'Oil on panel',1000.0000,1200.0000,NULL,NULL,NULL,'{\"safeSearchAnnotation\":{\"violence\":1,\"racy\":1},\"dominantColors\":[{\"color\":{\"red\":55,\"green\":48,\"blue\":26},\"web\":\"#37301A\",\"name\":\"Graphite\"},{\"color\":{\"red\":92,\"green\":78,\"blue\":50},\"web\":\"#5C4E32\",\"name\":\"West Coast\"},{\"color\":{\"red\":105,\"green\":80,\"blue\":36},\"web\":\"#695024\",\"name\":\"Horses Neck\"},{\"color\":{\"red\":128,\"green\":118,\"blue\":88},\"web\":\"#807658\",\"name\":\"Pablo\"},{\"color\":{\"red\":139,\"green\":112,\"blue\":61},\"web\":\"#8B703D\",\"name\":\"McKenzie\"},{\"color\":{\"red\":162,\"green\":153,\"blue\":120},\"web\":\"#A29978\",\"name\":\"Tallow\"}]}');
INSERT INTO `Paintings` (`PaintingID`,`ArtistID`,`GalleryID`,`ImageFileName`,`Title`,`ShapeID`,`MuseumLink`,`AccessionNumber`,`CopyrightText`,`Description`,`Excerpt`,`YearOfWork`,`Width`,`Height`,`Medium`,`Cost`,`MSRP`,`GoogleLink`,`GoogleDescription`,`WikiLink`,`JsonAnnotations`) VALUES (613,102,32,'102060','Old Woman and Boy with Candles',1,'https://www.mauritshuis.nl/en/explore/the-collection/artworks/old-woman-and-boy-with-candles-1150/','1150','Private Use Only','An old woman gazes ahead, shielding her eyes from the candlelight, while the boy behind her holds his candle, ready to be lit. The panel is painted in the style of Caravaggio, whose work Rubens had seen in Italy. This style is characterised by its exciting effects of light and unpolished naturalism.','An old woman gazes ahead, shielding her eyes from the candlelight, while the boy behind her holds his candle, ready to be lit. The panel is painted in the style of Caravaggio, whose work Rubens had seen in Italy. This style is characterised by its exciting effects of light and unpolished naturalism.',1616,63,77,'Oil on panel',1100.0000,1500.0000,NULL,NULL,NULL,'{\"safeSearchAnnotation\":{\"violence\":1,\"racy\":1},\"dominantColors\":[{\"color\":{\"red\":43,\"green\":26,\"blue\":15},\"web\":\"#2B1A0F\",\"name\":\"Wood Bark\"},{\"color\":{\"red\":51,\"green\":42,\"blue\":35},\"web\":\"#332A23\",\"name\":\"Coffee Bean\"},{\"color\":{\"red\":74,\"green\":41,\"blue\":24},\"web\":\"#4A2918\",\"name\":\"Indian Tan\"},{\"color\":{\"red\":138,\"green\":59,\"blue\":39},\"web\":\"#8a3b27\",\"name\":\"Fire\"},{\"color\":{\"red\":158,\"green\":108,\"blue\":71},\"web\":\"#9e6c47\",\"name\":\"Dark Tan\"},{\"color\":{\"red\":195,\"green\":156,\"blue\":114},\"web\":\"#c39c72\",\"name\":\"Fallow\"}]}');
INSERT INTO `Paintings` (`PaintingID`,`ArtistID`,`GalleryID`,`ImageFileName`,`Title`,`ShapeID`,`MuseumLink`,`AccessionNumber`,`CopyrightText`,`Description`,`Excerpt`,`YearOfWork`,`Width`,`Height`,`Medium`,`Cost`,`MSRP`,`GoogleLink`,`GoogleDescription`,`WikiLink`,`JsonAnnotations`) VALUES (614,102,32,'102070','Portrait of a Woman, possibly Clara Fourment',1,'https://www.mauritshuis.nl/en/explore/the-collection/artworks/portrait-of-a-woman-possibly-clara-fourment-15931643-1132/','1132','Private Use Only','This woman is probably Clara Fourment from Antwerp, and her husband Peter van Hecke is hanging on the left. They belonged to Rubens’ intimate family circle, as Clara was the elder sister of Rubens’ second wife Hélène. The couple clearly live in comfort, in view of their fashionable clothing of shiny black fabrics and fine lace collars, and Clara’s ostrich feather fan.','This woman is probably Clara Fourment from Antwerp, and her husband Peter van Hecke is hanging on the left. They belonged to Rubens’ intimate family circle, as Clara was the elder sister of Rubens’ second wife Hélène. The couple clearly live in comfort, in view of their fashionable clothing of shiny black fabrics and fine lace collars, and Clara’s ostrich feather fan.',1630,91,115,'Oil on panel',1000.0000,1400.0000,NULL,NULL,NULL,'{\"safeSearchAnnotation\":{\"violence\":1,\"racy\":1},\"dominantColors\":[{\"color\":{\"red\":21,\"green\":14,\"blue\":9},\"web\":\"#150E09\",\"name\":\"Bokara Grey\"},{\"color\":{\"red\":63,\"green\":47,\"blue\":24},\"web\":\"#3F2F18\",\"name\":\"Cola\"},{\"color\":{\"red\":85,\"green\":12,\"blue\":6},\"web\":\"#550C06\",\"name\":\"Red Oxide\"},{\"color\":{\"red\":102,\"green\":11,\"blue\":6},\"web\":\"#660B06\",\"name\":\"Maroon\"},{\"color\":{\"red\":68,\"green\":72,\"blue\":62},\"web\":\"#44483e\",\"name\":\"Armadillo\"},{\"color\":{\"red\":222,\"green\":180,\"blue\":141},\"web\":\"#deb48d\",\"name\":\"Brandy\"}]}');
