[ "PaintingID" => 369, "ArtistID" => 92, "FirstName" => "Filippo", "LastName" => "Lippi", "GalleryID" => 41, "GalleryName" => "Picture Gallery (Berlin)", "ImageFileName" => "092040", "Title" => "Adoration in the Forest", "ShapeID" => 3, "MuseumLink" => "http://www.smb-digital.de/eMuseumPlus?service=ExternalInterface&module=collection&objectId=864730&viewType=detailView", "AccessionNumber" => "69", "CopyrightText" => "Creative Commons Attribution-NonCommercial-ShareAlike 3.0", "Description" => "Adoration in the Forest is a painting completed before 1459 by the Carmelite friar, Filippo Lippi, of the Virgin Mary and the newly born Christ Child lying on the ground, in the unusual setting of a steep, dark, wooded wilderness. There are no shepherds, kings, ox, ass, there is no Joseph. It was painted for one of the wealthiest men in Renaissance Florence, the banker Cosimo de Medici. In later times it had a turbulent history. Hitler ordered it to be hidden in WW2 and it became part of the story of a mutiny in the U.S. Army.", "Excerpt" => "Adoration in the Forest is a painting completed before 1459 by the Carmelite friar, Filippo Lippi, of the Virgin Mary and the newly born Christ Child lying on the ground, in the unusual setting of a steep, dark, wooded wilderness.", "YearOfWork" => 1459, "Width" => 1185, "Height" => 1295, "Medium" => "Oil on wood", "Cost" => 1200.0000, "MSRP" => 1600.0000, "GoogleLink" => "http://www.googleartproject.com/collection/gemaldegalerie-staatliche-museen-zu-berlin/artwork/the-adoration-in-the-forest-filippo-lippi/331331/", "GoogleDescription" =>"", "WikiLink" => "http://en.wikipedia.org/wiki/Adoration_in_the_Forest_(Lippi)" ],          
 
[ "PaintingID" => 366, "ArtistID" => 92, "FirstName" => "Filippo", "LastName" => "Lippi", "GalleryID" => 4, "GalleryName" => "Uffizi Museum", "ImageFileName" => "092010", "Title" => "Madonna and Child", "ShapeID" => 1, "MuseumLink" => "http://www.uffizi.firenze.it/catalogo/", "AccessionNumber" => "1890, 1598", "CopyrightText" => "Public domain", "Description" => "Madonna with Child is a painting by the Italian Renaissance artist Filippo Lippi, executed around 1465. It is one of the few works by Lippi which was not executed with the help of his workshop and was an influential model for later depictions of the Madonna and Child, including those by Sandro Botticelli.", "Excerpt" =>"Madonna with Child is a painting by the Italian Renaissance artist Filippo Lippi, executed around 1465. It is one of the few works by Lippi which was not executed with the help of his workshop and was an influential model for later depictions of the Madonna and Child, including those by Sandro Botticelli.", "YearOfWork" => 1465, "Width" => 95, "Height" => 64, "Medium" => "Tempera on panel", "Cost" => 500.0000, "MSRP" => 600.0000, "GoogleLink" => "https://www.google.com/culturalinstitute/beta/asset/madonna-and-child-with-two-angels/9AE9qn4SE5pOPg", "GoogleDescription" => "A famous example of the artist's maturity, this painting was transferred to the Uffizi in 1796 from the Villa of Poggio Imperiale. Thanks to the spontaneity and naturalness of the composition, it reaped great interest as an iconographic model for numerous artists including the young Botticelli, apprenticed to the friar Lippi. On the back of the panel there is an autograph charcoal sketch of a female figure.", "WikiLink" => "http://en.wikipedia.org/wiki/Madonna_and_Child_(Fra_Lippo_Lippi)" ],

